import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-view-client',
  templateUrl: './view-client.component.html',
  styleUrls: ['./view-client.component.css']

})
export class ViewClientComponent implements OnInit {
clients:any[]=[];
message:string='';
  constructor(private http:HttpClient) { }


  ngOnInit(): void {
this.fetchClient();
  }


  fetchClient(){
    this.http.get('http://localhost:3000/getClient')
      .subscribe((response:any)=>
    {this.clients=response},
    (error)=>{console.error('Error fetching the client',error);}
  );
  }
  deleteClient(id:number){
    if(confirm('Are you sure you want to delete this client?')){
      this.http.delete('http://localhost:3000/deleteClient/'+id)
      .subscribe((response:any)=>
    {this.message=response.message;

      this.fetchClient();},

    (error)=>{console.error('Error deleting the client',error);}
  );
}
}
}
<head>
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.1/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-4bw+/aepP/YC94hEpVNVgiZdgIC5+VKNBQNGCHeKRQN+PtmoHDEXuppvnDJzQIu9" crossorigin="anonymous">
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.1/dist/js/bootstrap.bundle.min.js" integrity="sha384-HwwvtgBNo3bZJJLYd8oVXjrBZt8cqVSpeBNS5n7C8IVInixGAoxmnlMuBnhbgrkm" crossorigin="anonymous"></script>
</head>

{{message}}
<h1>View Clients</h1>
<table id="view">
<tr>
<th>ID</th>
<th>Name</th>
<th>Email</th>
<th>Address</th>
<th>Password</th>
<th>Repeat Password</th>
<th>Edit Action</th>
<th>Delete Action</th>
</tr>

<tr *ngFor="let client of clients">
<td>{{client.id}}</td>
<td>{{client.name}}</td>
<td>{{client.email}}</td>
<td>{{client.address}}</td>
<td>{{client.pswd}}</td>
<td>{{client.rpswd}}</td>

<td><button routerLink="/editClient/{{client.id}}">Edit</button></td>
<td><button (click)="deleteClient(client.id)">Delete</button></td>  
</tr>
</table>
