import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-edit-client',
  templateUrl: './edit-client.component.html',
  styleUrls: ['./edit-client.component.css']
})
export class EditClientComponent implements OnInit {
  id:number=0;
  name:string='';
  email:string='';
  address:string='';
  pswd:string='';
  rpswd:string='';
  message:string='';
  constructor(private http:HttpClient,private route:ActivatedRoute,private router:Router) { }


  ngOnInit(): void {
    this.route.paramMap.subscribe(params=>{
      const idParam=params.get('id');
      if(idParam!==null){
        this.id=+idParam;
        this.fetchClient();
      }
      else{
        console.error("id is missing  or null");
      }


    })
  }


fetchClient(){
  this.http.get('http://localhost:3000/getClient/'+this.id)
      .subscribe((response:any)=>
    {
      const client=response[0];
      this.name=client.name;
      this.email=client.email;
      this.address=client.address;
      this.pswd=client.pswd;
      console.log(this.pswd)
      this.rpswd=client.rpswd;
    },
    (error)=>{console.error('Error fetching the client',error);}
  );
}


  updateClient(){
    const client={
      id:this.id,
      name:this.name,
      email:this.email,
      address:this.address,
      pswd:this.pswd,
      rpswd:this.rpswd
    };


    this.http.put('http://localhost:3000/updateClient',client)
    .subscribe((response:any)=>
    {this.message=response.message;
    this.router.navigate(['/viewMeet'])},
    (error)=>{console.error('Error updating the client',error);}
  );


  }


}
<head>
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.1/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-4bw+/aepP/YC94hEpVNVgiZdgIC5+VKNBQNGCHeKRQN+PtmoHDEXuppvnDJzQIu9" crossorigin="anonymous">
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.1/dist/js/bootstrap.bundle.min.js" integrity="sha384-HwwvtgBNo3bZJJLYd8oVXjrBZt8cqVSpeBNS5n7C8IVInixGAoxmnlMuBnhbgrkm" crossorigin="anonymous"></script>
</head>
<body>
    <ul class="list-group list-group-horizontal">
        <li class="list-group-item"><a routerLink="/addClient">Add Client</a></li>
        <li class="list-group-item"><a routerLink="/viewClient">View Client</a></li>
        <li class="list-group-item"><a routerLink="/addMeet">Add Meeting</a></li>
        <li class="list-group-item"><a routerLink="/viewMeet">View Meeting</a></li>
    </ul>

<div class="container mt-5 pt-5">
    <div class="row">
      <div class="col-12 col-sm-7 col-md-6 m-auto">
        <div class="card border-0 shadow">
          <div class="card-body">


<h1 align="center"> Edit Client</h1>
<div>
    <label>ID:</label>
    <input type="number" [(ngModel)]="id" [disabled]="true"/>
</div>
<div>
    <label>Name:</label>
    <input type="text" [(ngModel)]="name"/>
</div>
<div>
    <label>Email:</label>
    <input type="text" [(ngModel)]="email"/>
</div>
<div>
    <label>Address:</label>
    <input type="text" [(ngModel)]="address"/>
</div>
<div>
    <label>Password:</label>
    <input type="text" [(ngModel)]="pswd"/>
</div>
<div>
    <label>Repeat Password:</label>
    <input type="text" [(ngModel)]="rpswd"/>
</div>
<div class="text-center mt-3">
    <button (click)="updateClient()" class="btn btn-primary">Update Client</button>
    <a class="nav-link">Click here for Update the Client</a>
  </div>

<div class="msg">
    {{message}}
</div>

          </div>
        </div>
    </div>
</div>
</div>
</body>
