<h1 align="center"> Add Meeting</h1>
<div>
    <label>ID:</label>
    <input type="number" [(ngModel)]="id" />
</div>
<div>
    <label>Meeting Topic:</label>
    <input type="text" [(ngModel)]="mtopic"/>
</div>
<div>
    <label>number of Person:</label>
    <input type="number" [(ngModel)]="nop"/>
</div>
<div>
    <label>Meeting Date:</label>
    <input type="date" [(ngModel)]="mdate"/>
</div>
<div>
    <label>Meeting Time:</label>
    <input type="time" [(ngModel)]="mtime"/>
</div>

<div class="text-center mt-3">
    <button (click)="addMeet()" class="btn btn-primary">Add Meeting</button>
    <a class="nav-link">Click here for Add the Meeting</a>
</div>
